pub enum Error {

    /// A custom, boxed error.
    Custom(BoxedErr),

    /// Wrong generation error.
    WrongGeneration(WrongGeneration),

    #[doc(hidden)]
    __NonExhaustive,
}

pub enum StorageEntry<'a, 'b:'a, T: 'a, D: 'a> {

    /// Entry variant that is returned if the entity has a component.
    Occupied(OccupiedEntry),

    /// Entry variant that is returned if the entity does not have a component.
    Vacant(VacantEntry),
}

pub enum ComponentEvent {

    /// An insertion event, note that a modification event will be triggered if
    /// the entity already had a component and had a new one inserted.
    Inserted(Index),

    /// A modification event, this will be sent any time a component is accessed
    /// mutably so be careful with joins over `&mut storages` as it could
    /// potentially flag all of them.
    Modified(Index),

    /// A removal event.
    Removed(Index),
}
